const initialState = {
    workItems: []
}

const workReducer = (state = initialState, action) => {
    switch (action.payload) {
        case 'ADD_TASK_WORK':
            state = {...state, workItems: state.workItems.concat(action.payload)};
            break;
        case 'DELETE_TASK_WORK':
            state = state.workItems.slice();
            state.splice(action.payload, 1);
            break;
        default:
            return state;
    }
}

export default workReducer;
