const workReducer = (state = {}, action) => {
    switch (action.payload) {
        case 'ADD_TASK_WORK':
            state = state.concat(action.payload);
            break;
        case 'DELETE_TASK_WORK':
            state = state.slice();  
            state.splice(action.payload, 1);
            break;
        default:
            return state;
    }
}

export default workReducer;
