import React, { PureComponent } from 'react';
import '../App.css';
import { addTask as addTaskOwn } from '../actions/actionOwn';
// import { addTask as addTaskEducation } from '../actions/actionEducation';
import { addTask as addTaskWork } from '../actions/actionWork';

import { connect } from 'react-redux';
import PropTypes from 'prop-types';


class AddForm extends PureComponent {
	static PropTypes = {
		addTaskOwn: PropTypes.func.isRequired,
		addTaskEducation: PropTypes.func.isRequired,
		addTaskWork: PropTypes.func.isRequired
	}

	constructor() {
		super();
		this.state = {
			task: ''
		};

		this.onChange = this.onChange.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
	}


	onChange(e) {
		this.setState({ [e.target.name]: e.target.value })
	}

	onSubmit(e) {
		e.preventDefault();

		const task = {
			task: this.state.task
		}

		this.props.addTaskOwn(task);
		this.props.addTaskEducation(task);
		this.props.addTaskWork(task);
	}

	render() {
		return (
			<div>
				<form onSubmit={this.onSubmit}>
					<label>Add task:</label>
					<input name="task" type="text" onChange={this.onChange} value={this.state.task} />
					<button type="submit">Add task</button>
				</form>
			</div>
		)
	}
}




export default connect (null, {addTaskOwn, addTaskEducation, addTaskWork})(AddForm);
