 const localStorageMiddleware = ({ getState }) => {
    return next => action => {
        const result = next(action);
        if ([ action.payload ].includes(result.type)) {
            localStorage.setItem('state', JSON.stringify(getState()))
        }
        return result;
    };
};

export default localStorageMiddleware;
