const initialState = {
    ownItems: []
}

const onwReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD_TASK_OWN':
            return { ...state, ownItems: state.ownItems.concat(action.payload) };

        case 'DELETE_TASK_OWN':
            const ownItems = state.ownItems.slice();
            ownItems.splice(action.payload, 1);
            return { ...state, ownItems };

        default:
            return state;
    }
}

export default onwReducer;
